        function monthDiff(d1, d2) {
            var months;
            months = (d2.getFullYear() - d1.getFullYear()) * 12;
            months -= d1.getMonth() + 1;
            months += d2.getMonth();
            return months+1;
        }
        function getMonth(mon){
            switch(mon.toUpperCase())
            {
                case 'JAN':
                    mon='01';
                    break;
                case 'FEB':
                    mon='02';
                    break;
                case 'MAR':
                    mon='03';
                    break;
                case 'APR':
                    mon='04';
                    break;
                case 'MAY':
                    mon='05';
                    break;
                case 'JUN':
                    mon='06';
                    break;
                case 'JUL':
                    mon='07';
                    break;
                case 'AUG':
                    mon='08';
                    break;
                case 'SEP':
                    mon='09';
                    break;
                case 'OCT':
                    mon='10';
                    break;
                case 'NOV':
                    mon='11';
                    break;
                case 'DEC':
                    mon='12';
                    break;

            }
            return mon;
        }


