function process_nesl(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
{
    try
    {

        //document.getElementById("om").innerHTML+="loading";
        var mon=count[n].split('-')[0];
        var m=count[n].split('-')[1];
        var d=count[n].split('-')[2];
        var yy=count[n].split('-')[3];
        var y=yy.substr(2,2);
        //import files from modules
        var fs = require('fs');
        var csv = require('ya-csv');
        var async = require('async');
        //var path = require('./node_modules/path');

        //var writeFileStream = fs.createWriteStream('./tempData/output.txt',{flag:'a',encoding:'utf-8'});

    

        async.parallel({
            data1 : function(callback){
                if (fs.existsSync(downloadpath+'nsxbhavcopy'+d+m+yy+'.csv')) {
                    Ext.getCmp('pbar3').updateText('Processing nsxbhavcopy'+d+m+yy+'.csv');
                    Ext.getCmp('pbar3').updateProgress(0.05);
                    fs.readFile(downloadpath+'nsxbhavcopy'+d+m+yy+'.csv','utf-8', function(err, data){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
                        }
                        callback(err, data);
                    });
                }else
                {
                    // Here readdir's functionaliity has no use. It is written for only to send
                    // '0' data in its callback function because we cannot call "callback(err, data);"
                    // function out side a callback function
                    fs.readdir('html', function(err, files){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
                        }

                        callback(err,'0');
                    });
                }
            }

        }, function(err, result){

            if(err)
            {
                document.getElementById("om").innerHTML+="<br/>An error occured while processing "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
            }

            if (fs.existsSync(downloadpath+'nsxbhavcopy'+d+m+yy+'.csv')) {
                Ext.getCmp('pbar3').updateText('Processing nsxbhavcopy'+d+m+yy+'.csv');

                var arrRow = new Array();
                arrRow = result['data1'].split('\n');

                var arrFileData = new Array();

                // arrRow.lenght-3 is for last 3 lines of no use in file
                for(var i=0 ; i<arrRow.length-1;i++)
                {
                    var tempArr = arrRow[i].split(',');
                    arrFileData.push(tempArr);
                }


                // add blank space where cell of company name col not have value
                for(i=0; i<arrFileData.length; i++)
                {
                    if(arrFileData[i][12] == null || arrFileData[i][12] == "")
                    {
                        arrFileData[i][12] = '';
                    }
                }


                var outputpath1=outputpath;
                var filename = outputpath1+'National_Spot_nesl'+d+m+yy+'.csv';
                var header='TICKER,NAME,DATE,OPEN,HIGH,LOW,CLOSE,VOLUME,OPENINT';
                if(!fs.existsSync(filename)){
                    fs.writeFile(filename, header+'\n', function (err)
                    {
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while processing "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
                        }


                        var date =yy+m+d;
                        // Writing data to output file

                        for(i=1;i<arrFileData.length;i++)
                        {
                            fs.appendFileSync(filename,arrFileData[i][1]+','+arrFileData[i][1]+','+date+','+arrFileData[i][2]+','+arrFileData[i][3]+','+arrFileData[i][4]+','+arrFileData[i][5]+','+parseInt(arrFileData[i][7])+','+parseInt(arrFileData[i][9])+'\n','UTF-8', function (err) {
                                if(err)
                                {
                                    document.getElementById("om").innerHTML+="<br/>An error occured while processing "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
                                }

                            // console.log('| Appended to file!');
                            });
                        }// for
				
						
                        Ext.getCmp('pbar3').updateText('Processing nsxbhavcopy'+d+m+yy+'.csv completed');

                    });
                }
            }
        });


        document.getElementById("om").innerHTML+="<br/>nsel"+d+m+yy+".csv is processed..";
        addlog(logpath, 'nsxbhavcopy'+d+m+yy+'.csv is processed..');
        n++;
        if(count.length>n)
        {
            loadprogressbar();
            setTimeout(function (){
                process_nesl(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
                }, 1000);
            document.getElementById("om").innerHTML+='..';
        }
        else
        {
            sn++;
            callfun(count,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn);
        }

        function monthDiff(d1, d2) {
            var months;
            months = (d2.getFullYear() - d1.getFullYear()) * 12;
            months -= d1.getMonth() + 1;
            months += d2.getMonth();
            return months+1;
        }
        function getMonth(mon){
            switch(mon.toUpperCase())
            {
                case 'JAN':
                    mon='01';
                    break;
                case 'FEB':
                    mon='02';
                    break;
                case 'MAR':
                    mon='03';
                    break;
                case 'APR':
                    mon='04';
                    break;
                case 'MAY':
                    mon='05';
                    break;
                case 'JUN':
                    mon='06';
                    break;
                case 'JUL':
                    mon='07';
                    break;
                case 'AUG':
                    mon='08';
                    break;
                case 'SEP':
                    mon='09';
                    break;
                case 'OCT':
                    mon='10';
                    break;
                case 'NOV':
                    mon='11';
                    break;
                case 'DEC':
                    mon='12';
                    break;

            }
            return mon;
        }
		
    }
    catch(err)
    {
        document.getElementById("om").innerHTML+="<br/>An error occured while processing "+downloadpath+"nsxbhavcopy"+d+m+yy+".csv";
        n++;
        if(count.length>n)
        {
            loadprogressbar();
            setTimeout(function (){
                process_nesl(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
                }, 1000);
            document.getElementById("om").innerHTML+='..';
        }
        else
        {
            sn++;
            callfun(count,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn);
        }
    }
    }