Ext.require('Ext.form.*');

Ext.onReady(function() {
    Ext.widget('form', {
        renderTo: 'example-form',
        title: 'Number fields with spinner',
        bodyPadding: 5,
        frame: true,
        width: 340,
        fieldDefaults: {
            labelAlign: 'left',
            labelWidth: 105,
            anchor: '100%'
        },
        items: [{
             
                xtype: 'textfield',
                fieldLabel: 'Without spinner',
                id:'txtbox',
                name: 'without_spinner',
                listeners: {change: getVal}
            }]

    });
function getVal(txt)
     {
         $("#txtTicker").val(txt.value);
         
     }
});

